INSERT INTO `command` VALUES ('start', '1', 'Syntax: .start\r\n\r\nTeleport you to the starting area of your character.', '0');
INSERT INTO `command` VALUES ('taxicheat', '5', 'Syntax: .taxicheat on/off\r\n\r\nTemporary grant access or remove to all taxi routes for the selected character. If no character is selected, hide or reveal all routes to you.\r\n\r\nVisited taxi nodes sill accessible after removing access.', '0');
INSERT INTO `command` VALUES ('linkgrave', '5', 'Syntax: .linkgrave $graveyard_id [alliance|horde]\r\n\r\nLink current zone to graveyard for any (or alliance/horde faction ghosts). This let character ghost from zone teleport to graveyard after die if graveyard is nearest from linked to zone and accept ghost of this faction. Add only single graveyard at another map and only if no graveyards linked (or planned linked at same map).', '0');
INSERT INTO `command` VALUES ('neargrave', '5', 'Syntax: .neargrave [alliance|horde]\r\n\r\nFind nearest graveyard linked to zone (or only nearest from accepts alliance or horde faction ghosts).', '0');
INSERT INTO `command` VALUES ('explorecheat', '5', 'Syntax: .explorecheat $flag\r\n\r\nReveal  or hide all maps for the selected player. If no player is selected, hide or reveal maps to you.\r\n\r\nUse a $flag of value 1 to reveal, use a $flag value of 0 to hide all maps.', '0');
INSERT INTO `command` VALUES ('hover', '5', 'Syntax: .hover $flag\r\n\r\nEnable or disable hover mode for your character.\r\n\r\nUse a $flag of value 1 to enable, use a $flag value of 0 to disable hover.', '0');
INSERT INTO `command` VALUES ('levelup', '1', 'Syntax: .levelup [$playername] [$numberoflevels]\r\n\r\nIncrease/decrease the level of character with $playername (or the selected if not name provided) by $numberoflevels Or +1 if no $numberoflevels provided). If $numberoflevels is omitted, the level will be increase by 1. If $numberoflevels is 0, the same level will be restarted. If no character is selected and name not provided, increase your level. Command can be used for offline character. All stats and dependent VALUESrecalculated. At level decrease talents can be reset if need. Also at level decrease equipped items with greater level requirement can be lost.', '2');
INSERT INTO `command` VALUES ('showarea', '5', 'Syntax: .showarea $areaid\r\n\r\nReveal the area of $areaid to the selected character. If no character is selected, reveal this area to you.', '0');
INSERT INTO `command` VALUES ('hidearea', '5', 'Syntax: .hidearea $areaid\r\n\r\nHide the area of $areaid to the selected character. If no character is selected, hide this area to you.', '0');
INSERT INTO `command` VALUES ('additem', '1', 'Syntax: .additem $itemid/[$itemname]/shift-click-item-link $itemcount\r\n\r\nAdds the specified number of items of id $itemid (or exact (!) name $itemname in brackets, or link created by shift-click at item in inventory or recipe) to your or selected character inventory. If $itemcount is omitted, only one item will be added.\r\n.', '2');
INSERT INTO `command` VALUES ('additemset', '1', 'Syntax: .additemset $itemsetid\r\n\r\nAdd items from itemset of id $itemsetid to your or selected character inventory. Will add by one example each item from itemset.', '2');
INSERT INTO `command` VALUES ('bank', '1', 'Syntax: .bank\r\n\r\nShow your bank inventory.', '0');
INSERT INTO `command` VALUES ('wchange', '1', 'Syntax: .wchange $weathertype $status\r\n\r\nSet current weather to $weathertype with an intensity of $status.\r\n\r\n$weathertype can be 1 for rain, 2 for snow, and 3 for sand. $status can be 0 for disabled, and 1 for enabled.', '0');
INSERT INTO `command` VALUES ('ticket', '1', 'Syntax: .ticket $subcommand\nType .ticket to see the list of possible subcommands or .help ticket $subcommand to see info on subcommands', '0');
