REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009007, 'Ah ... what a great spot to rest. Goodnight Azeroth.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '');
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009006, 'Greetings Brother. Please, forgive my appearance - I mean you no harm. I\'ve had a long and difficult journey, and I am weary from my travels. I just need to rest a while. I will be no bother to you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '');
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009005, 'This place was a home for me for many years. What a wonderous sight.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '');
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009004, 'Was it left or right to the cathedral? Hmmm ... right... I think.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '');
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009003, 'Ah ... I missed this city. *Takes a deep breath and soaks in the view* ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '');
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009002, 'Just passing through, Jonathan. You look as ... stoic ... as ever.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '');
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009010, 'Hmmm ... do I have time to grab a Deviate from the bank? No no ... I need to rest. Tomorrow!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009011, 'Lok\'tar Doras! Thanks for all the free gryphon rides. Don\'t worry - I never told Thrall. *Tosses Doras a bag of coins*', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009012, 'Was the Valley of Spirits left or right? It\'s been so long....left... I think.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009013, 'The barracks! I was given my most prestigeous rank here during my time with the Horde. What a powerful moment in my life to be recognized for all that hard work! Nobody stood a chance against me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009014, 'Should I goto the huts?  ... No, no. I feel like sleeping beneath the stars tonight.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009015, 'Mok-rah warrior! I\'m just going to rest over there by the rocks. Fight on my brother.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009016, 'Ah ... this looks like a great place to rest my eyes. Goodnight Azeroth.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009009, 'Lok\'tar! For the Horde! I formed many friendships and bonds in this very city. Defeat quite a few foes here too.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
REPLACE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000009008, 'My old stomping grounds! The number of duels I won out here were countless. Those were good times.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
